import React from 'react';
import './FeaturedAttractions.css';
import { Link } from 'react-router-dom';

const attractions = [
    {
        name: 'Գառնու տաճար',
        description: 'Դասական հելլենիստական ​​տաճար Գառնիում, Հայաստան։',
        image: 'https://www.remotelands.com/travelogues/app/uploads/2021/12/Garni-Roman-Temple-2.jpg'
    },
    {
        name: 'Սևանա լիճ',
        description: 'Եվրասիայի ամենամեծ քաղցրահամ բարձրադիր լճերից մեկը։',
        image: 'https://armenia-tour.com/storage/media/Destinations/Sevan/shutterstock_1442321309.jpg'
    },
    {
        name: 'Տաթևի վանք',
        description: '9-րդ դարի վանք, որը գտնվում է բազալտե մեծ սարահարթի վրա։',
        image: 'https://upload.wikimedia.org/wikipedia/commons/c/cd/Tatev_Monastery_from_a_distance.jpg'
    },
    {
        name: 'Նորավանք',
        description: '13-րդ դարի հայկական վանք, որը գտնվում է նեղ կիրճում։',
        image: 'https://armenia.travel/_next/image/?url=https%3A%2F%2Fapi.armenia.travel%2Fstorage%2FArtboard-19.jpg&w=3840&q=75'
    },
    {
        name: 'Խոր Վիրապ',
        description: 'Հայկական վանք, որը գտնվում է Արարատյան դաշտում։',
        image: 'https://www.advantour.com/img/armenia/ararat/khor-virap2.jpg'
    }
];

const FeaturedAttractions = () => {
    return (
        <div className="attractions-container">
            <Link to='/temple'>
            <div className="attractions-list">
                {attractions.map((attraction, index) => (
                    <div key={index} className="attraction-item">
                        <img src={attraction.image} alt={attraction.name} />
                        <h3>{attraction.name}</h3>
                        <p>{attraction.description}</p>
                    </div>
                ))}
            </div>
            </Link>
        </div>
    );
};

export default FeaturedAttractions;
