import React, { useEffect, useState } from 'react';
import './workspace.css';

const WorkspacePage = () => {
  const [workspaces, setWorkspaces] = useState([]);
  const [name, setName] = useState('');
  const [slug, setSlug] = useState('');
  const [error, setError] = useState('');

  const createWorkspace = () => {
    if (name === '' || slug === '') {
      setError('Both name and slug are required.');
      return;
    }

    if (workspaces.find(workspace => workspace.slug === slug)) {
      setError('Slug is already taken.');
      return;
    }

    const newWorkspace = {
      id: Date.now(),
      name,
      slug
    };

    setWorkspaces([...workspaces, newWorkspace]);

    setName('');
    setSlug('');
    setError('');
  };

  const deleteWorkspace = (id) => {
    setWorkspaces(workspaces.filter(workspace => workspace.id !== id));
  };

  useEffect(() => {
    const fetchWorkspaces = async () => {
      try {
        const response = await fetch('http://localhost:3001/workspace');
        console.log()
        const data = await response.json();
        setWorkspaces(data);
      } catch (error) {
        console.error('Error fetching workspaces:', error);
      }
    };

    fetchWorkspaces();
  }, []);

  return (
    <div className="workspace-container">
      <h1>Manage Your Workspaces</h1>

      <div className="workspace-list">
        {workspaces.length > 0 ? (
          workspaces.map((workspace) => (
            <div className="workspace-card" key={workspace.id}>
              <h3>{workspace.name}</h3>
              <p>Slug: {workspace.slug}</p>
              <button className="btn-delete" onClick={() => deleteWorkspace(workspace.id)}>
                Delete
              </button>
            </div>
          ))
        ) : (
          <p className="no-workspaces">No workspaces created yet.</p>
        )}
      </div>

      <div className="workspace-form">
        <h2>Create New Workspace</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            createWorkspace();
          }}
        >
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Slug:</label>
            <input
              type="text"
              value={slug}
              onChange={(e) => setSlug(e.target.value)}
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit" className="btn-submit">
            Create Workspace
          </button>
        </form>
      </div>
    </div>
  );
};

export default WorkspacePage;
