import React, { useState } from "react";
import { Form, Input, Button, Card, Typography } from "antd";
import { Link, useNavigate } from "react-router-dom";
import Logo1 from "../../assets/images/logo1.png";
import "./auth.css";

const { Title } = Typography;

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [loginError, setLoginError] = useState("");
  const [loginSuccessed, setLoginSuccessed] = useState(false);

  const handleLogin = async () => {
    try {
      const { email, password } = formData;

      setLoginSuccessed(true);
      const response = await fetch("http://localhost:3001/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
        credentials: 'include'
      });
      // if (!response.ok) {
      //   const errorData = await response.json();
      //   // Check for specific error message
      //   if (errorData.message === "Incorrect password") {
      //     setLoginError("Գաղտնաբառը սխալ է:");
      //   } else {
      //     setLoginError(errorData.message || "Network response was not ok");
      //   }
      //   return;
      // }

      const data = await response.json();
      localStorage.setItem("user", JSON.stringify(data));
      localStorage.setItem("isLoggedIn", "true");
      localStorage.setItem("accesToken", data.accesToken);
      navigate("/");
    } catch (error) {}
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleLogin();
  };

  return (
    <div className="auth-container">
      <Card className="auth-card">
        <div className="card-content">
          <div className="form-container">
            <Title level={2}>Մուտք</Title>
            <Form layout="vertical" onSubmit={handleSubmit}>
              <Form.Item
                label="email"
                name="email"
                rules={[
                  {
                    required: true,
                    message: "Please input your email!",
                  },
                ]}>
                <Input
                  type="email"
                  placeholder="email"
                  className="input"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                />
              </Form.Item>
              <Form.Item
                label="password"
                name="password"
                rules={[
                  {
                    required: true,
                    message: "Please input your password!",
                  },
                ]}>
                <Input.Password
                  placeholder="password"
                  className="input"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                />
              </Form.Item>
              {loginError && <div className="login-error">{loginError}</div>}

              <Form.Item>
                {loginSuccessed ? (
                  <Link to={"/"}>
                    <Button
                      type="primary"
                      htmlType="submit"
                      block
                      onClick={handleSubmit}>
                      Մուտք
                    </Button>
                  </Link>
                ) : (
                  <Button
                    type="primary"
                    htmlType="submit"
                    block
                    onClick={handleSubmit}>
                    Մուտք
                  </Button>
                )}
              </Form.Item>
            </Form>
            <div style={{ marginTop: "16px" }}>
            Don't have an account? <Link to="/register">Գրանցվել</Link>
            </div>
          </div>
          <div className="logo-containerr">
            <Link to="/">
              <img src={Logo1} alt="Logo" className="auth-logo" />
            </Link>
          </div>
        </div>
      </Card>
    </div>
  );
};

export default Login;
