import './restaurants.css'

const Restaurants = () => {
    const restaurantsData = [
      {
      profileImg: 'https://sakurageorgia.com/storage/app/uploads/public/5d0/e29/ebe/5d0e29ebefe49485966820.png',
      title: 'Lavash Restaurant',
      location: 'Երևան'
    },
    {
      profileImg: 'https://cf.bstatic.com/xdata/images/hotel/max1024x768/207730643.jpg?k=a61b92eeb1128e8e225eacf95337f5bb6f4c1ea861422b269e886bf604142524&o=&hp=1',
      title: 'Republica Hotel Yerevan',
      location: 'Երևան',
    },
    {
      profileImg: 'https://cf.bstatic.com/xdata/images/hotel/max1024x768/88522788.jpg?k=674559e2e283d6fcf7f3b5b894ac99eda92f52246cc0ee5d68f5aade18be5f99&o=&hp=1',
      title: 'Grand Hotel Yerevan',
      location: 'Երևան',
    },
    {
      profileImg: 'https://example.com/hotel2-profile.jpg',
      title: 'Hotel 2',
      location: 'Location 2',
    },
    {
      profileImg: 'https://example.com/hotel2-profile.jpg',
      title: 'Hotel 2',
      location: 'Location 2',
    },
    {
      profileImg: 'https://example.com/hotel2-profile.jpg',
      title: 'Hotel 2',
      location: 'Location 2',
    },
  ];

  const fetchRestaurants = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/restaurants", {
        method: 'GET',
        headers: {
          "Content-Type": "application/json",
          "authorization": `Barear ${localStorage.getItem("accesToken")}`
        },
      })
      const data = await response.json();

      console.log(data);
      return await response.json();
    } catch(error) {
     console.error(error)
    }
  }

  const [restaurants, setResturants] = useState(fetchRestaurants)



  return (
      <>
        <PageTitleSelect title='Ռեստորաններ' />
        <div className="restaurants-page">
          { restaurantsData.map((restaurant, index) => (
            <ProfileCard key={index} {...restaurant} />
          ))}
        </div>
      </>
    );
  };


export {Restaurants}
