import React from 'react';
import DetailPage from '../../../components/DetailPage/DetailPage';
import PageTitle from '../../../components/PageTitle/pageTitle';

const yvnInfo = {
    title: 'Երևան',
    description: [
      <b>Երևան</b>, ', Հայաստանի մայրաքաղաքն ու խոշորագույն բնակավայրը։ Գտնվում է երկրի կենտրոնական մասում՝ Հրազդանի երկու ափերին։ Հանդիսանում է երկրի վարչաքաղաքական, տնտեսական կենտրոնը։ 2022 թվականի մարդահամարի տվյալների համաձայն՝ Երևանում ապրում է ▲1 092 800 մարդ (2022):',
      <br />,
      'Երևանը՝ որպես Էրեբունի ամրոց֊քաղաք, հիմնադրել է Վանի թագավորության արքա Արգիշտի Ա-ն մ. թ. ա. 8-րդ դարում Էրեբունի անվամբ։',
      <br />,
      'Երևանն աշխարհի ', <b>հնագույն</b>,' քաղաքներից է։ Ժամանակագրական առումով՝ քաղաքի տարածքի հնագույն բնակավայրը Երևանյան կամ Հրազդան-1 քարայրն է (Երևանյան լճի մոտ՝ Հրազդան գետի ձախ ափին, ներկայումս ԱՄՆ դեսպանատան տարածքում), որի հնագույն՝ ստորին շերտը (4–5 մշակութային շերտ) թվագրվում է մուստերյան շրջանով (100–35 հազարամյակ առաջ)։ Ընդհանուր առմամբ Հրազդանի կիրճում քարայրների թիվը մոտ հարյուր է։ Որպես հին բնակավայր հայտնի է նաև Շենգավիթը. այստեղ մարդիկ բնակություն են հաստատել 5–6 հազար տարի առաջ։',<br />,
      'Հայ մատենագիրները՝ վկայակոչելով քաղաքային լեգենդը Երևանի անվան ծագումը երկար ժամանակ կապել են Նոյյան տապանի աստվածաշնչյան ավանդության հետ. իբրև երբ Նոյի տապանը կանգ է առել Արարատի գագաթին, Նոյը, ջրհեղեղից հետո տեսնելով առաջին ցամաքը, որն ապագա Երևանի կառուցման վայրն էր, գոչել է՝ երևա՜ց (այստեղից էլ՝ «Երևան» անվանումը)։',
      <br />,
      'Առավել տարածված է այն տեսակետը, որ «Երևան» անունը կապված է Էրեբունի բերդաքաղաքի անվան հետ։ Սակայն կան նաև այլ տեսակետներ. դրանցից մեկի համաձայն՝ հիմնադրված նոր բնակավայրի անվանակոչման համար Արգիշտի Ա-ն կամ օգտագործել է տարածքում եղած ու նրան հայտնի հնագույն այլ բնակավայրի անուն, կամ կոչել է «երի» կամ «արի» ցեղի (երկրի) անունով։ Երևանը կոչվել է նաև Էրիվան, Էրևան, Ռևան, Իրավան, Այրիվան և այլ անուններով։',
      <br />,
      'Երևանի պատմության մեջ առանձնահատուկ տեղ է գրավում ', <b>խորհրդային շրջանը</b>,'։ ՀԽՍՀ ղեկավարները փորձում էին Երևանը գեղեցկացնել և աշխարհի այլ մայրաքաղաքների շարքում իր անունն առանձնացնել։ Սարգիս Կասյանի օրոք՝ 1920–1921 թվականներին մեծ աշխատանք կատարվեց արտադրական ձեռնարկությունները գործի գցելու, սովին ու համաճարակին վերջ տալու, դպրոցները բացելու, ջրմուղը կարգի բերելու, փողոցներն ու հրապարակները մաքրելու համար։ Ալեքսանդր Մյասնիկյանի հրավերով Հայաստան են տեղափոխվում և Երևանում բնակություն հաստատում նկարիչ Մարտիրոս Սարյանը, կոմպոզիտոր Ալեքսանդր Սպենդիարյանը, բանաստեղծուհի Շուշանիկ Կուրղինյանը, ճարտարապետ Ալեքսանդր Թամանյանը և շատ ուրիշներ։ Թամանյանին վիճակված էր Երևանի նոր հատակագծի նախագծումը։',
      <br />
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/0/08/Yerevan_map_by_Tamanyan.jpg'
  };

const Yerevan = () =>{
    return(
        <div>
            <PageTitle title='Երևան' />
            <DetailPage 
                description={yvnInfo.description} 
                image={yvnInfo.image} 
            />
        </div>
    )
}

export {Yerevan}
