import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faLanguage, faMoneyBillAlt, faCar, faExclamationTriangle } from '@fortawesome/free-solid-svg-icons';
import './travelTips.css';

const tips = [
    { icon: faCalendarAlt, title: 'Այցելության լավագույն ժամանակը', content: 'Հայաստան այցելելու լավագույն ժամանակը գարնանն է (ապրիլ-հունիս) և աշունը (սեպտեմբեր-նոյեմբեր), երբ եղանակը հաճելի է, և փառատոները եռում են:' },
    { icon: faLanguage, title: 'Լեզու', content: 'Հայաստանի պաշտոնական լեզուն հայերենն է։ Այնուամենայնիվ, շատ մարդիկ, հատկապես զբոսաշրջային վայրերում, խոսում են անգլերեն և ռուսերեն:' },
    { icon: faMoneyBillAlt, title: 'Արժույթ', content: 'Հայաստանում օգտագործվող արժույթը հայկական դրամն է: Վարկային քարտերը լայնորեն ընդունված են Երևանում, բայց լավ է կանխիկ գումար ունենալ գյուղական վայրերի համար:' },
    { icon: faCar, title: 'Տրանսպորտ', content: 'Երևանում լավ զարգացած է հասարակական տրանսպորտը՝ ավտոբուսներով, միկրոավտոբուսներով և տաքսիներով։ Միջքաղաքային ճանապարհորդության համար մտածեք գնացքների կամ ընդհանուր տաքսիների օգտագործման մասին:' },
    { icon: faExclamationTriangle, title: 'Անվտանգություն', content: 'Հայաստանն ընդհանուր առմամբ ապահով երկիր է զբոսաշրջիկների համար։ Այնուամենայնիվ, ինչպես ցանկացած այլ միջավայրում, կարևոր է տեղյակ լինել ձեր շրջապատի մասին և ձեռնարկել ընդհանուր անվտանգության նախազգուշական միջոցներ:' }
];

const TravelTips = () => {
    return (
        <div className="tips-container">
            <h2>ՕԳՏԱԿԱՐ ՏԵՂԵԿՈՒԹՅՈՒՆՆԵՐ</h2>
            <div className="tips-list">
                {tips.map((tip, index) => (
                    <div key={index} className="tip-item">
                        <FontAwesomeIcon icon={tip.icon} size="5x" color='white'/>
                        <h3>{tip.title}</h3>
                        <p>{tip.content}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TravelTips;
