import React from 'react';
import DetailPage from '../../../components/DetailPage/DetailPage';
import PageTitle from '../../../components/PageTitle/pageTitle';

const armenianInfo = {
  title: 'Հայեր',
  description: [
    <b>Հայեր</b>, ', էթնիկ խումբ, որը սերել ու կենսագործում է Հայկական լեռնաշխարհում և Հարավային Կովկասում։',
    <br />,
    'Հայերը մեծամասնություն են կազմում Հայաստանում։ Հայերը ցրված են ամբողջ աշխարհում և ունեն 6-8 միլիոնանոց սփյուռք, որի անդամները միշտ կամ ժամանակ առ ժամանակ ապրում են Հայաստանի սահմաններից դուրս։ Հայկական սփյուռքի ամենամեծ կենտրոններ են Ռուսաստանը, Ամերիկայի Միացյալ Նահանգները, Ֆրանսիան, Վրաստանը, Իրանը, Գերմանիան, Ուկրաինան, Լիբանանը, Բրազիլիան և Սիրիան։ Բացի Իրանի և նախկին խորհրդային երկրների գաղթավայրերից, մնացած գաղթավայրերը ստեղծվել են հիմնականում Հայոց ցեղասպանության հետևանքով։',
    <br />,
    'Հայերի մեծամասնությունը ',
    <b>Հայ առաքելական եկեղեցու</b>,
    ' հետևորդ է, որը աշխարհի ամենահին եկեղեցիներից մեկն է։ Հիսուսի մահից քիչ անց քրիստոնեությունն սկսել է տարածվել հայերի մեջ։ Հայաստանում քրիստոնեություն են տարածել Հիսուսի երկու առաքյալները՝ Թադեոսը և Բարդուղիմեոսը։ 301թ.-ին Արշակունիների թագավորության Տրդատ III արքայի օրոք Հայաստանը դարձավ առաջին պետությունը, որը պաշտոնապես ընդունեց քրիստոնեությունը՝ որպես պետական կրոն։',
    <br />,
    <b>Հայերենը</b>,' պատկանում է Հնդեվրոպական լեզվաընտանիքին։ Այն բաժանված է երկու մասի՝ արևելահայերեն և արևմտահայերեն։ Արևելահայերենը տարածված է Հայաստանում, Արցախում, Իրանում և հետխորհրդային երկրներում, իսկ արևմտահայերենն օգտագործվել է պատմական Արևմտյան Հայաստանում, իսկ այժմ խոսվում է ցեղասպանության արդյունքում առաջացած գաղթավայրերում։',
    <br />,
    'Հայոց գրերը ստեղծվել են Մեսրոպ Մաշտոցի կողմից՝ 405 թվականին։'
  ],
  image: 'https://pbs.twimg.com/media/DjYAWghWsAEJ0Xg.jpg'
};

function Arm() {
  return (
    <div>
      <PageTitle title='Հայեր' />
      <DetailPage 
        description={armenianInfo.description} 
        image={armenianInfo.image} 
      />
    </div>
  );
}

export { Arm };
