import React, { useEffect, useState } from 'react';
// import './workspace.css';


const WorkspacePage = () => {
  const [workspaces, setWorkspaces] = useState([]);
  const [name, setName] = useState('');
  const [slug, setSlug] = useState('');
  const [error, setError] = useState('');

  const createWorkspace = () => {
    if (name === '' || slug === '') {
      setError('Both name and slug are required.');
      return;
    }

    if (workspaces.find(workspace => workspace.slug === slug)) {
      setError('Slug is already taken.');
      return;
    }

    const newWorkspace = {
      id: Date.now(), // unique id
      name,
      slug
    };

    setWorkspaces([...workspaces, newWorkspace]);

    // Reset form and error
    setName('');
    setSlug('');
    setError('');
  };

  // Function to delete a workspace
  const deleteWorkspace = (id) => {
    setWorkspaces(workspaces.filter(workspace => workspace.id !== id));
  };

  useEffect(async () => {
    const allWorkspaces = await fetch('http://localhost:3001/workspace');
    const data = await allWorkspaces.json();
    setWorkspaces(data);
  }, []);

  return (
    <div>
      <h1>Workspaces</h1>

      {/* List of workspaces */}
      <ul>
        {workspaces.map((workspace) => (
          <li key={workspace.id}>
            {workspace.name} ({workspace.slug})
            <button onClick={() => deleteWorkspace(workspace.id)}>Delete</button>
          </li>
        ))}
      </ul>

      {/* Form for creating a new workspace */}
      <div>
        <h2>Create New Workspace</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            createWorkspace();
          }}
        >
          <div>
            <label>
              Name:
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </label>
          </div>
          <div>
            <label>
              Slug:
              <input
                type="text"
                value={slug}
                onChange={(e) => setSlug(e.target.value)}
                required
              />
            </label>
          </div>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <button type="submit">Create Workspace</button>
        </form>
      </div>
    </div>
  );
};

export default WorkspacePage;
