import React from 'react';
import PageTitle from '../../../components/PageTitle/pageTitle';
import DetailPagee from '../../../components/DetailPage/DetailPagee';
import './infoPage.css';

const temples = [
  {
    title: 'Գառնու Տաճար',
    description: [
      'Գառնու տաճարը, որը կառուցվել է 1-ին դարում, հելլենիստական ճարտարապետության բացառիկ օրինակ է Հայաստանում։ Այն գտնվում է Կոտայքի մարզում։',
      <br />,
      'Տաճարը կանգնեցվել է արքա Տրդատ I-ի կողմից և նվիրված էր Միհր աստծուն՝ Հայկական փառասիրական պանթեոնի արեգակնային աստվածին։ Գառնին միակ կանգուն պահպանված հեթանոսական տաճարն է Հայաստանում և ամենահայտնիներից մեկը երկրում։',
      <br />,
      'Տաճարը հայտնի է իր մոնումենտալ և գեղագիտական արժանիքներով։ Դրա դասական տառապարանը և սյուները ներշնչված են հին հունական ճարտարապետությունից։ Տաճարի տարածքում նաև պահպանվել են հնագիտական այլ գտածոներ։',
      <br />,
      'Տաճարի շրջակայքում կարող եք այցելել նաև Գառնիի ամրոցը և հնագիտական այլ տեսարժան վայրեր, ներառյալ մոզաիկ հատակները և անտիկ բաղնիքները։',
      <br />,
      'Գառնու տաճարի հարակից տարածքում կա մի հնագույն բաղնիք, որտեղ պահպանվել են հին մոզաիկները՝ պատկերող հունական աստվածներին և առասպելական տեսարաններ։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/1/10/Garni-msu-wlm-3121.jpg/1920px-Garni-msu-wlm-3121.jpg',
  },
  {
    title: 'Էջմիածնի Մայր Տաճար',
    description: [
      'Էջմիածնի Մայր Տաճարը, որը համարվում է աշխարհի առաջին քրիստոնեական տաճարներից մեկը, հիմնադրվել է 301-303 թվականներին Գրիգոր Լուսավորչի կողմից։',
      <br />,
      'Տաճարը կառուցվել է հայոց Տրդատ Գ արքայի ժամանակաշրջանում և դարձել է Հայաստանի հոգևոր կենտրոնը։ Այն գտնվում է Արմավիրի մարզում։',
      <br />,
      'Տաճարը հայտնի է իր եզակի ճարտարապետությամբ և արվեստի գործերով։ Տարածքում պահպանվել են բազմաթիվ կրոնական և մշակութային արժեքներ, այդ թվում՝ սրբավայրեր և սրբազան մասունքներ։',
      <br />,
      'Էջմիածնի Մայր Տաճարը նաև Հայաստանի Առաքելական Եկեղեցու կենտրոնն է, որտեղ գտնվում է Կաթողիկոսարանի նստավայրը։',
      <br />,
      'Տաճարը ընդգրկված է ՅՈՒՆԵՍԿՕ-ի համաշխարհային ժառանգության ցանկում։ Այցելելով այստեղ՝ դուք կարող եք տեսնել նաև տաճարի թանգարանները, որտեղ ցուցադրված են միջնադարյան արվեստի և պատմության բազմաթիվ արժեքավոր նմուշներ։',
      <br />,
      'Տաճարը հայտնի է նաև իր հարուստ գրադարանով, որտեղ պահվում են հնագույն հայկական ձեռագրեր։ Այստեղից նաև կարելի է տեսնել Գրիգոր Լուսավորչի Վեհարանը։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6f/%D4%B7%D5%BB%D5%B4%D5%AB%D5%A1%D5%AE%D5%B6%D5%AB_%D5%84%D5%A1%D5%B5%D6%80_%D5%8F%D5%A1%D5%B3%D5%A1%D6%80.jpg/1920px-%D4%B7%D5%BB%D5%B4%D5%AB%D5%A1%D5%AE%D5%B6%D5%AB_%D5%84%D5%A1%D5%B5%D6%80_%D5%8F%D5%A1%D5%B3%D5%A1%D6%80.jpg',
  },
  {
    title: 'Տաթևի վանական համալիր',
    description: [
      'Տաթևի վանական համալիրը, որը հիմնադրվել է 9-րդ դարում, գտնվում է Սյունիքի մարզում։ Տաթևը եղել է Հայաստանի կարևորագույն հոգևոր, մշակութային և գիտական կենտրոններից մեկը։',
      <br />,
      'Վանքը առանձնանում է իր ճարտարապետական բարդություններով և գեղագիտական վեհությամբ։ Տարածքում կան բազմաթիվ եկեղեցիներ, վանական կառույցներ և բերդեր։',
      <br />,
      'Տաթևի վանական համալիրը հայտնի է իր պատմական և մշակութային արժեքներով։ Այստեղ նաև պահպանվել են միջնադարյան հայկական ձեռագրերի և գեղարվեստի բազմաթիվ օրինակներ։',
      <br />,
      'Տաթևի վանքն ունի նաև մի քանի եզակի կառույցներ, այդ թվում՝ Տաթևի թռիչք (Tatev Wings) ճոպանուղին, որը համարվում է աշխարհի ամենաերկար հետադարձ ճանապարհով ճոպանուղին։',
      <br />,
      'Այցելելով Տաթև՝ դուք կարող եք վայելել նաև շրջակայքի հրաշալի բնությունը և անտառապատ լեռները։',
      <br />,
      'Տաթևի վանքի մոտակայքում կարող եք այցելել նաև Տաթևի անապատը՝ ևս մեկ կարևոր հնագիտական վայր, որտեղ պահպանվել են հնագույն կառույցներ և ձեռագրեր։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/c/cd/Tatev_Monastery_from_a_distance.jpg',
  },
  {
    title: 'Նորավանքի վանական համալիր',
    description: [
      'Նորավանքի վանական համալիրը գտնվում է Վայոց ձորի մարզում և հիմնադրվել է 13-րդ դարում։',
      <br />,
      'Վանքը հայտնի է իր ճարտարապետությամբ, հատկապես իր գեղեցիկ եկեղեցիներով, որոնք զարդարված են հնագույն խաչքարերով և գեղարվեստական դեկորներով։',
      <br />,
      'Նորավանքը հանդիսանում է հայկական մշակութային և հոգևոր ժառանգության կարևորագույն կենտրոններից մեկը։',
      <br />,
      'Վանքը կառուցված է կարմիր քարով, որն արտահայտում է շրջակայքի վառ գույնը և դառնում է ավելի գեղեցիկ արևամուտի և արևածագի ժամանակ։',
      <br />,
      'Նորավանքի մոտակայքում կարող եք տեսնել նաև Ամաղուի քարանձավային համալիրները, որոնք հետաքրքրություն են առաջացնում որպես բնական և պատմական հուշարձաններ։',
      <br />,
      'Նորավանքը հայտնի է նաև իր Աստվածածին եկեղեցու յուրահատուկ մուտքով, որը հայտնի է որպես մեկ էկլեզիայի եկեղեցի։'
    ],
    image: 'https://armadventure.com/wp-content/uploads/2018/10/noravank-1.jpg',
  },
  {
    title: 'Հաղպատի վանական համալիր',
    description: [
      'Հաղպատի վանական համալիրը գտնվում է Լոռու մարզում և հիմնադրվել է 10-րդ դարում։',
      <br />,
