import React from 'react';
import DetailPage from '../../../components/DetailPage/DetailPage';
import PageTitle from '../../../components/PageTitle/pageTitle';

const cultInfo = {
    title: 'Մշակույթ',
    description: [
      <b>Հայկական մշակույթը</b>, ' հայ ժողովրդի ստեղծած նյութական և հոգևոր արժեքների ամբողջությունն է։ Հայերը դարերով ստեղծել են մշակութային արժեքներ, որոնք արտացոլված են գրականության, երաժշտության, պարի, կերպարվեստի և ճարտարապետության մեջ։',
      <br />,
      <b >Հայերենը</b>, ' հնդեվրոպական լեզվաընտանիքի առանձին ճյուղ է։ Այն ունի 2 հիմնական գրավոր ձև՝ գրաբար և աշխարհաբար։',
      ' Հայերենն ունի յուրահատուկ այբուբեն, որը ստեղծվել է 405-406 թթ. Մեսրոպ Մաշտոցի կողմից։ Այն բաղկացած է 39 տառից: Մեսրոպ Մաշտոցը նաև մեծապես նպաստել է հայերեն գրականության և դպրության զարգացմանը՝ թարգմանելով Աստվածաշունչը հայերեն և ստեղծելով առաջին դպրոցները։',
      <br />,
      <b>Գրականություն։ </b>, 
      '5-րդ դարում Մովսես Խորենացին ստեղծեց «Հայոց պատմություն» գործը։ Միջնադարյան գրականությունը կարևոր դեր է ունեցել ազգային ինքնության պահպանման գործում։ Ժամանակակից հայ գրականության հայտնի ներկայացուցիչներն են Հովհաննես Թումանյանը, Ավետիք Իսահակյանը և Պարույր Սևակը։',
      <br />,
      <b>Երաժշտություն։ </b>, 
      'Հայ երաժշտությունը ընդգրկում է ինչպես ժողովրդական, այնպես էլ դասական և ժամանակակից ժանրերը։ Հայկական ժողովրդական երաժշտության մեջ առանձնահատուկ տեղ ունեն ազգային գործիքները՝ դուդուկը, զուռնան, քամանչան և դհոլը։ Կոմիտասը համարվում է հայկական երաժշտության հիմնադիրը, ով հավաքեց և մշակեց բազմաթիվ ժողովրդական երգեր։ Ժամանակակից հայ երաժշտության ներկայացուցիչներից են Արամ Խաչատրյանը, Տիգրան Մանսուրյանը և Սերժ Թանկյանը, ովքեր իրենց ստեղծագործություններով ճանաչում են բերել հայկական երաժշտությանը միջազգային մակարդակում։',
      <br />,
      <b>Պար։ </b>, 
      'ՀՀայկական պարը բազմազան և ինքնատիպ է՝ արտահայտելով ազգային հոգևորությունն ու պատմական անցքերը։ Ամենահայտնի պարերն են Քոչարին, Յարխուշտան և Շալախոն։',
      <br />,
      <b>Կերպարվեստ։ </b>, 
      'Հ Հայկական կերպարվեստը ներառում է գեղանկարչություն, քանդակագործություն և մանրանկարչություն։ Հայկական մանրանկարչությունը հայտնի է իր մանրամասնությամբ և գունային բազմազանությամբ, իսկ խաչքարերը՝ հայկական քանդակագործության վառ օրինակներից են։ Գեղանկարչության մեջ նշանակալի գործիչներ են Մարտիրոս Սարյանը և Հակոբ Հակոբյանը։',
      <br />,
      <b>Ավանդույթներ և սովորույթներ։ </b>, 
      'Հայկական ավանդույթները և սովորույթները մեծապես ձևավորված են քրիստոնեության ազդեցությամբ։ Տոները, ինչպիսիք են Զատիկը, Վարդավառը և Խաչվերացը, կարևոր դեր ունեն հայերի կյանքում։ Ավանդական հայկական հարսանիքները հայտնի են իրենց հարուստ ծիսակարգերով և տոնականություններով։ Հայկական խոհանոցը նույնպես արտացոլում է ազգային ավանդույթները։ Հայկական հյուրասիրությունը հայտնի է իր ջերմությամբ և մեծահոգությամբ։',
    ],
    image: 'https://i0.wp.com/www.armgeo.am/wp-content/uploads/2017/12/rsz_display_image.jpg'
  }


const Culture = () => {
  return (
    <div>
      <PageTitle title='Մշակույթ' />
      <DetailPage 
          description={cultInfo.description} 
          image={cultInfo.image} 
        />
    </div>
  );
}

export { Culture };
