import React from 'react';
import PageTitle from '../../../components/PageTitle/pageTitle';
import DetailPagee from '../../../components/DetailPage/DetailPagee';
import './infoPage.css';

const museums = [
  {
    title: 'Հայաստանի պատմության թանգարան',
    description: [
      'Հայաստանի պատմության թանգարան, պատմամշակութային, գիտահետազոտական խոշոր կենտրոն է։ Հիմնադրվել է 1919 թվականին, Երևանում։',
      <br />,
      'Հայաստանի պատմության թանգարանը հիմնադրվել է Հայաստանի Առաջին Հանրապետության պառլամենտի օրենքով (N 439, 09.09.1919)։ Կոչվել է Ազգագրական-մարդաբանական թանգարան-գրադարան։ Առաջին տնօրենը եղել է Երվանդ Լալայանը։ Այցելուների համար բացվել է 1921 թվականի օգոստոսի 20-ին։',
      <br />,
      'Թանգարանը կազմավորվել է Կովկասի Հայոց ազգագրական ընկերության, Նոր Նախիջևանի Հայկական հնությունների թանգարանների, Անիի Հնադարանի, Վաղարշապատի Մատենադարանի հավաքածուների հիման վրա (15.289 առարկա)։',
      <br />,
      'Թանգարանում պահպանվում է շուրջ 400 000 առարկայից բաղկացած ազգային հավաքածու հետևյալ բաժիններով. հնագիտություն (հիմնական հավաքածուի 35%,) ազգագրություն (8%), դրամագիտություն (45%), վավերագրեր (12%)։ Թանգարանի հավաքածուները համալրվում են հիմնականում Հայաստանի տարածքի հնավայրերից հայտնաբերված գտածոներով, գնումներով, նվիրատվություններով։ Թանգարանը ներկայացնում է Հայաստանի մշակույթի և պատմության ամբողջական պատկերը նախապատմական ժամանակներից՝ մեկ միլիոն տարի առաջ, մինչև մեր օրերը։ Ցուցադրում է Հայկական լեռնաշխարհում Հին Արևելքի երկրների (Եգիպտոս, Միտանի, Խեթական թագավորություն, Ասորեստան, Իրան, Սելևկյան պետություն, Հռոմ, Բյուզանդիա) մշակութային փոխառնչությունների հազվագյուտ հետքերը։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/1/1a/Armenia_Museum_of_Art_and_History.jpg/1920px-Armenia_Museum_of_Art_and_History.jpg',
  },
  {
    title: 'Գաֆէսճեան արվեստի կենտրոն',
    description: [
      'Գաֆէսճեան արվեստի կենտրոն (ԳԱԿ), ժամանակակից արվեստի կենտրոն Հայաստանի մայրաքաղաք Երևանի Կենտրոն թաղամասում գտնվող Կասկադ համալիրում։ Բացվել է 2009 թվականի նոյեմբերի 9-ին։ ԳԱԿ-ը հիմնադրվել է ամերիկահայ գործարար Ջերարդ Գաֆէսճեանի կողմից և ներկայացնում է Ջերարդ Գաֆէսճեանի ժամանակակից արվեստի հավաքածուն։ Գաֆէսճեան արվեստի կենտրոնը Ջերարդ Գաֆէսճեանի ամենախոշոր բարեգործական նախաձեռնություններից մեկն է։ Վերականգնելով Երևան քաղաքում գտնվող Կասկադ համալիրը՝ Գաֆէսճեանն այն դարձրեց ժամանակակից արվեստի կենտրոնի, որը ներկայացնում է ցուցադրություններ և բազմազան կրթական, մշակութային և համերգային ծրագրեր և միջոցառումներ բոլոր տարիքային խմբերի համար։',
      <br />,
      'Այս ճարտարապետական կասկադ ոճով կառուցված շինությունը, որտեղ այսօր գտնվում է Գաֆէսճեան արվեստի կենտրոնը, հայտնի է որպես Կասկադ համալիր։ Կասկադ համալիրի գաղափարի նախահեղինակը ճարտարապետ Ալեքսանդր Թամանյանն է (1878-1936)։ Նա ցանկանում էր միմյանց կապել քաղաքի հյուսիսային և կենտրոնական հատվածները՝ քաղաքի պատմականորեն բնակելի և մշակութային կենտրոնները ջրվեժների ու պարտեզների հսկայական կանաչ տարածքով, որը պետք է «գահավիժեր» քաղաքի ամենաբարձր բլուրներից մեկից։ Դժբախտաբար, այս նախագիծը խոր մոռացության մատնվեց մինչև 1970-ականների վերջը, երբ այն կրկին կյանքի կոչվեց Երևանի գլխավոր ճարտարապետ Ջիմ Թորոսյանի կողմից։ Կասկադ ոճի թորոսյանական ընկալումն ներառում էր Թամանյանի նախնական նախագիծը, բայց նաև լի էր նոր գաղափարներով՝ մոնումենտալ արտաքին աստիճանները, ներսի երկար շարժասանդուղքներով թունելը, բակերի և բացօթյա պարտեզների բարդ ցանցը՝ զարդարված հայոց հարուստ պատմության և մշակութային ժառանգության մոտիվները կրող բազմաթիվ զարդաքանդակներով։',
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/0/0e/2014_Erywa%C5%84%2C_Park_przy_Kaskadach_%2817%29.jpg/1920px-2014_Erywa%C5%84%2C_Park_przy_Kaskadach_%2817%29.jpg',
  },
  {
    title: 'Մատենադարան',
    description: [
      'Մատենադարան, հին ձեռագրերի և փաստաթղթերի պահպանման և ուսումնասիրման կենտրոն, ձեռագրերի պահպանման աշխարհի ամենահարուստ կենտրոններից մեկը։ Ունի ձեռագրերի և միջնադարյան գրքերի աշխարհի ամենախոշոր հավաքածուներից մեկը։ Այստեղ պահպանվում են շուրջ 23000 ձեռագիր, հմայիլներ, պատառիկներ և 300 000 արխիվային փաստաթուղթ։ Ամեն տարի Մատենադարանն ունենում է շուրջ 500000 այցելու։',
      <br />,
      'Հայերեն ձեռագիր մատյանների ամենախոշոր պահոցն է։ Այն ստեղծվել է 1921 թվականին՝ 5-րդ դարում ստեղծված Էջմիածնի մատենադարանի հիմքի վրա, և եղել է առաջին գիտահետազոտական հաստատությունը Հայաստանում։ Նախկինում այն կոչվել է Կուլտուր-պատմական ինստիտուտ։',
      <br />,
