const HotelDetailPage = () => {
  const { id } = useParams();
  const [hotel, setHotel] = useState(null);
  const [mainImage, setMainImage] = useState("");
  const navigate = useNavigate();

  const fetchHotel = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/hotels/${id}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${localStorage.getItem("accesToken")}`
        }
      });
      alert(response.status)
      if(response.status === "403") {
        alert('mta')
        navigate("/login")
      }
      const hotel = await response.json();
      setHotel(hotel);
      if (hotel && hotel.profileImg) {
        setMainImage(hotel.profileImg);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleThumbnailClick = (img) => {
    setMainImage(img);
  };

  useEffect(() => {
    fetchHotel();
  }, []);

  if (!hotel) {
    return <div>Loading...</div>;
  }

  return (
    <div className="hotel-detail-container">
      <div className="hotel-detail-main">
        <img src={mainImage} alt={hotel.title} className="hotel-detail-img" />
        <div className="hotel-detail-text">
          <div className="hotel-detail-header">
            <h1>{hotel.title}</h1>
          </div>
          <p className="hotel-detail-description">{hotel.description}</p>
          <p className="hotel-detail-location">Location: {hotel.location}</p>
        </div>
      </div>
      <div className="hotel-detail-thumbnails">
        {hotel.images && hotel.images.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`${hotel.title} ${index + 1}`}
            className="hotel-thumbnail"
            onClick={() => handleThumbnailClick(img)}
          />
        ))}
      </div>
    </div>
  );
};

export default HotelDetailPage;
