import React, { useState, useEffect } from 'react';
import styles from './workspace.module.css'; 

const WorkspacePage = () => {
  const [workspaces, setWorkspaces] = useState([]);
  const [name, setName] = useState('');
  const [slug, setSlug] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchWorkspaces = async () => {
      try {
        const response = await fetch('http://localhost:3001/workspace');
        const data = await response.json();
        setWorkspaces(data);
      } catch (error) {
        console.error('Error fetching workspaces:', error);
      }
    };

    fetchWorkspaces();
  }, []);

  const createWorkspace = () => {
    if (name === '' || slug === '') {
      setError('Both name and slug are required.');
      return;
    }

    if (workspaces.find(workspace => workspace.slug === slug)) {
      setError('Slug is already taken.');
      return;
    }

    const newWorkspace = {
      id: Date.now(),
      name,
      slug
    };

    setWorkspaces([...workspaces, newWorkspace]);
    setName('');
    setSlug('');
    setError('');
  };

  const deleteWorkspace = (id) => {
    setWorkspaces(workspaces.filter(workspace => workspace.id !== id));
  };

  return (
    <div className={styles.workspaceContainer}>
      <h1 className={styles.heading}>Manage Your Workspaces</h1>

      <div className={styles.workspaceList}>
        {workspaces.length > 0 ? (
          workspaces.map((workspace) => (
            <div className={styles.workspaceCard} key={workspace.id}>
              <h3>{workspace.name}</h3>
              <p>Slug: {workspace.slug}</p>
              <button className={styles.btnDelete} onClick={() => deleteWorkspace(workspace.id)}>
                Delete
              </button>
            </div>
          ))
        ) : (
          <p className={styles.noWorkspaces}>No workspaces created yet.</p>
        )}
      </div>

      <div className={styles.workspaceForm}>
        <h2>Create New Workspace</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            createWorkspace();
          }}
        >
          <div className={styles.formGroup}>
            <label>Name:</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className={styles.formGroup}>
            <label>Slug:</label>
            <input
              type="text"
              value={slug}
              onChange={(e) => setSlug(e.target.value)}
              required
            />
          </div>
          {error && <p className={styles.error}>{error}</p>}
          <button type="submit" className={styles.btnSubmit}>
            Create Workspace
          </button>
        </form>
      </div>
    </div>
  );
};

export default WorkspacePage;
