import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { Menu, Avatar, Dropdown, Button } from "antd";
import { UserOutlined } from "@ant-design/icons";
import "./navbar.css";
import Logo from "../../assets/images/logo1.png";

const { SubMenu } = Menu;
const selectedColor = "black";

const items = [
  { label: <Link to="/">Գլխավոր</Link>, key: "home" },
  {
    label: (
      <Link to="/about" style={{ color: "black" }}>
        Հայաստանի մասին
      </Link>
    ),
    key: "about",
    children: [
      { label: <Link to="/arm">Հայեր</Link>, key: "history" },
      { label: <Link to="/qrist">Քրիստոնևթյուն</Link>, key: "culture" },
      { label: <Link to="/yvn">Երևան</Link>, key: "culture" },
      { label: <Link to="/culture">Հայկական մշակույթ</Link>, key: "culture" },
      { label: <Link to="/museum">Թանգարաններ</Link>, key: "culture" },
      { label: <Link to="/temple">Տաճարներ և վանքեր</Link>, key: "culture" },
      {
        label: <Link to="/unesco">ՅՈՒՆԵՍԿՕ-ի ժառանգության</Link>,
        key: "culture",
      },
      { label: <Link to="/arch">Ճարտարապետություն</Link>, key: "culture" },
      { label: <Link to="/cuisine">Հայկական խոհանոց</Link>, key: "culture" },
    ],
  },
  { label: <Link to="/hotels">Հյուրանոցներ</Link>, key: "hotels" },
  { label: <Link to="/restaurants">Ռեստորաններ</Link>, key: "restaurants" },
  { label: <Link to="/monuments">Տեսարժան վայրեր</Link>, key: "monuments" },
];

const NavBar = () => {
  const location = useLocation();
  const currentPath = location.pathname;

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const user = localStorage.getItem("user");
    setIsLoggedIn(!!user);
  }, []);

  const handleLogout = async () => {
    try {
    const response = await fetch(`http://localhost:3001/api/auth/logout`, {
      method: 'DELETE',
      headers: {
        "Content-Type": "application/json",
      },
      credentials: 'include'
    });

    if(response.statusCode === 400) {
      throw new Error('Invalid');
    }
    localStorage.removeItem("user");
    localStorage.removeItem("accesToken")
    setIsLoggedIn(false);
    }catch(e){
      console.log("Error", e.message);
    }
  };

  let selectedKey =
    items.find((item) => {
      if (item.key === "about") {
        return currentPath.startsWith("/about");
      }
      return (
        item.label.props.to === currentPath ||
        (item.children &&
          item.children.some((child) => child.label.props.to === currentPath))
      );
    })?.key || "home";

  const loggedInMenu = (
    <Menu>
      <Menu.Item key="logout" onClick={handleLogout}>
        Դուրս գալ
      </Menu.Item>
    </Menu>
  );

  return (
    <div className="navbar-container">
      <Link to="/" className="logo-container">
        <img src={Logo} alt="Logo" className="logo" />
      </Link>
      <Menu
        mode="horizontal"
        selectedKeys={[selectedKey]}
        style={{ border: "none", flex: 1 }}>
        {items.map((item) => (
          <React.Fragment key={item.key}>
            {item.children ? (
              <SubMenu title={item.label}>
                {item.children.map((child) => (
                  <Menu.Item key={child.key}>
                    <Link to={child.label.props.to}>
                      {child.label.props.children}
                    </Link>
                  </Menu.Item>
                ))}
              </SubMenu>
            ) : (
              <Menu.Item key={item.key}>
                <Link
                  to={item.label.props.to}
                  style={{
                    color: selectedKey === item.key ? selectedColor : "black",
                    textDecoration: "none",
                  }}>
                  {item.label.props.children}
                </Link>
              </Menu.Item>
            )}
          </React.Fragment>
        ))}
      </Menu>
      <div className="avatar-container">
        {isLoggedIn ? (
          <Dropdown overlay={loggedInMenu} placement="bottomRight">
            <Avatar
              style={{
                width: "40px",
                height: "40px",
                cursor: "pointer",
              }}
              icon={<UserOutlined />}
            />
          </Dropdown>
        ) : (
          <Button type="primary">
            <Link to="/login" style={{ color: "white" }}>
              Մուտք
            </Link>
          </Button>
        )}
      </div>
    </div>
  );
};

export default NavBar;
