import React from 'react';
import PageTitle from '../../../components/PageTitle/pageTitle';
import DetailPagee from '../../../components/DetailPage/DetailPagee';
import './infoPage.css';

const cuisine = [
  {
    title: 'Խաշ',
    description: [
      'Խաշը ավանդական հայկական ապուր է, որը պատրաստվում է տավարի կամ ոչխարի ոտքերից։',
      <br />,
      'Խաշը համարվում է առողջարար սնունդ և սովորաբար մատուցվում է վաղ առավոտյան՝ աշնան և ձմռան ամիսներին։',
      <br />,
      'Այն սովորաբար ուտում են սխտորով, քացախով և լավաշով։'
    ],
    image: 'https://cdn.am.sputniknews.ru/img/2236/10/22361070_88:0:1512:1068_1920x0_80_0_0_14ef91077a666eb3d1f380ff8adc17cc.jpg',
  },
  {
    title: 'Դոլմա',
    description: [
      'Դոլման հայկական խոհանոցում հայտնի ուտեստ է, որը պատրաստվում է խաղողի տերևների մեջ փաթաթված մսով և բրնձով։',
      <br />,
      'Դոլման ունի բազմաթիվ տարբերակներ, և այն կարող է պատրաստվել տարբեր մսերով և բանջարեղեններով։',
      <br />,
      'Դոլման հաճախ պատրաստվում է տոնական միջոցառումների ժամանակ։'
    ],
    image: 'https://www.xohanoc.info/wp-content/uploads/2023/02/big-4.png',
  },
  {
    title: 'Հարիսա',
    description: [
      'Հարիսան ավանդական հայկական կերակուր է, որը պատրաստվում է կաթի մեջ տապակած ցորենի և մսից։',
      <br />,
      'Հարիսան ունի շատ խիտ և հագեցած համ, ինչը նրան դարձնում է սիրելի ուտեստ։',
      <br />,
      'Այն հայտնի է նաև որպես կերակուր, որը պատրաստվում է Սուրբ Կարապետի տոնին։'
    ],
    image: 'https://media-cdn.tripadvisor.com/media/photo-m/1280/21/57/d7/63/caption.jpg',
  },
  {
    title: 'Լավաշ',
    description: [
      'Լավաշը հայկական ավանդական հաց է, որը թխվում է թոնիրում։',
      <br />,
      'Լավաշը շատ բարակ և փափուկ հաց է, որը պատրաստվում է առանց խմորի։',
      <br />,
      'Լավաշը ՅՈՒՆԵՍԿՕ-ի համաշխարհային ժառանգության մաս է կազմում։'
    ],
    image: 'https://armenianweekly.b-cdn.net/wp-content/uploads/2023/07/IMG_4366-1024x768.jpg',
  },
  {
    title: 'Խորոված',
    description: [
      'Խորովածը հայկական խոհանոցում ամենահայտնի ուտեստներից մեկն է, որը պատրաստվում է գազանի, տավարի, խոզի կամ հավի մսից։',
      <br />,
      'Խորովածը հաճախ պատրաստվում է բացօթյա խարույկի վրա, որը նրան տալիս է հատուկ համ և բույր։',
      <br />,
      'Հայկական ընտանիքներում խորովածը պատրաստվում է հատուկ միջոցառումների և հավաքույթների ժամանակ։'
    ],
    image: 'https://images.unsplash.com/photo-1675929678215-e1a1f90d48f8?q=80&w=1974&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
  },
  {
    title: 'Գաթա',
    description: [
      'Գաթան հայկական քաղցրավենիք է, որը պատրաստվում է ալյուրից, կարագից և շաքարից։',
      <br />,
      'Գաթան ունի տարբեր տեսակներ՝ տարբեր շրջաններում տարբեր ձևերով պատրաստվում է։',
      <br />,
      'Գաթան հիմնականում պատրաստվում է տոնական օրերի և հայկական հարսանիքների ժամանակ։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/cf/Suikerbrood_van_Geghard._-_Armenia_%282910350766%29.jpg/1920px-Suikerbrood_van_Geghard._-_Armenia_%282910350766%29.jpg',
  },
  {
    title: 'Մատնաքաշ',
    description: [
      'Մատնաքաշը հայկական ավանդական հաց է, որը ճանաչված է իր օվալաձև կամ կլորաձև ձևով։',
      <br />,
      'Մատնաքաշը ունի փափուկ խմոր, որը պատրաստվում է պարզ բաղադրիչներից՝ ալյուր, ջուր, խմորիչ, աղ։',
      <br />,
      'Մատնաքաշը հաճախ օգտագործվում է որպես ուղեկցող տարբեր հայկական ուտեստների։'
    ],
    image: 'https://www.sas.am/upload/iblock/079/0b16dabbf14a9338f944ebe168f667dd.jpg',
  },
];

const Cuisine = () => {
  return (
    <>
      <PageTitle title="Հայկական Խոհանոց" />
      <div className="info-page">
        {cuisine.map((dish, index) => (
          <DetailPagee
            key={index}
            title={dish.title}
            description={dish.description}
            image={dish.image}
          />
        ))}
      </div>
    </>
  );
};

export { Cuisine };
