import React from 'react';
import PageTitle from '../../../components/PageTitle/pageTitle';
import DetailPagee from '../../../components/DetailPage/DetailPagee';
import './infoPage.css';

const unesco = [
  {
    title: 'Էջմիածնի Մայր Տաճար',
    description: [
      'Էջմիածնի Մայր Տաճարը, որը համարվում է աշխարհի առաջին քրիստոնեական տաճարներից մեկը, հիմնադրվել է 301-303 թվականներին Գրիգոր Լուսավորչի կողմից։',
      <br />,
      'Տաճարը կառուցվել է հայոց Տրդատ Գ արքայի ժամանակաշրջանում և դարձել է Հայաստանի հոգևոր կենտրոնը։ Այն գտնվում է Արմավիրի մարզում։',
      <br />,
      'Տաճարը հայտնի է իր եզակի ճարտարապետությամբ։ Տարածքում պահպանվել են բազմաթիվ կրոնական և մշակութային արժեքներ, այդ թվում՝ սրբավայրեր և սուրբ մասունքներ։',
      <br />,
      'Էջմիածնի Մայր Տաճարը նաև Հայ Առաքելական Եկեղեցու կենտրոնն է, որտեղ գտնվում է Կաթողիկոսարանի նստավայրը։',
      <br />,
      'Տաճարը ընդգրկված է ՅՈՒՆԵՍԿՕ-ի համաշխարհային ժառանգության ցանկում։ Այցելելով այստեղ՝ դուք կարող եք տեսնել նաև տաճարի թանգարանները, որտեղ ցուցադրված են միջնադարյան արվեստի և պատմության բազմաթիվ արժեքավոր նմուշներ։',
      <br />,
      'Տաճարը հայտնի է նաև իր հարուստ գրադարանով, որտեղ պահվում են հնագույն հայկական ձեռագրեր։ Այստեղ նաև կարելի է տեսնել Գրիգոր Լուսավորչի Վեհարանը։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6f/%D4%B7%D5%BB%D5%B4%D5%AB%D5%A1%D5%AE%D5%B6%D5%AB_%D5%84%D5%A1%D5%B5%D6%80_%D5%8F%D5%A1%D5%B3%D5%A1%D6%80.jpg/1920px-%D4%B7%D5%BB%D5%B4%D5%AB%D5%A1%D5%AE%D5%B6%D5%AB_%D5%84%D5%A1%D5%B5%D6%80_%D5%8F%D5%A1%D5%B3%D5%A1%D6%80.jpg',
  },
  {
    title: 'Հաղպատի վանական համալիր',
    description: [
      'Հաղպատի վանական համալիրը գտնվում է Լոռու մարզում և հիմնադրվել է 10-րդ դարում։',
      <br />,
      'Վանքը հայտնի է իր միջնադարյան ճարտարապետությամբ և գեղարվեստական արժեքներով։ Այստեղ պահպանվել են բազմաթիվ եկեղեցիներ, գրքեր, խաչքարեր և այլ գեղարվեստական գործեր։',
      <br />,
      'Հաղպատը ընդգրկված է ՅՈՒՆԵՍԿՕ-ի համաշխարհային ժառանգության ցանկում։',
      <br />,
      'Հաղպատի վանքը հատկապես հայտնի է իր թանգարանային հավաքածուներով, որտեղ պահվում են հին ձեռագրեր և կրոնական արվեստի գործեր։',
      <br />,
      'Հաղպատի տարածքում կարող եք նաև տեսնել ճարտարապետության բազմաթիվ օրինակներ, որոնք վկայում են հայկական մշակույթի մեծության մասին։',
      <br />,
      'Հաղպատի մոտակայքում կարող եք այցելել նաև Ալավերդի քաղաք, որը նույնպես հարուստ է պատմամշակութային հուշարձաններով։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/7/7d/Monasterio_de_Haghpat%2C_Armenia%2C_2016-09-30%2C_DD_17.jpg/1920px-Monasterio_de_Haghpat%2C_Armenia%2C_2016-09-30%2C_DD_17.jpg',
  },
  {
    title: 'Սանահինի վանական համալիր',
    description: [
      'Սանահինի վանական համալիրը հիմնադրվել է 10-րդ դարում և գտնվում է Լոռու մարզում։',
      <br />,
      'Վանքը հայտնի է իր պատմամշակութային արժեքներով։ Տարածքում պահպանվել են բազմաթիվ եկեղեցիներ, խաչքարեր և ձեռագրեր։',
      <br />,
      'Սանահինն ընդգրկված է ՅՈՒՆԵՍԿՕ-ի համաշխարհային ժառանգության ցանկում։',
      <br />,
      'Վանքի տանիքները և գմբեթները առանձնանում են գեղեցիկ զարդաքանդակներով, ինչը այն դարձնում է միջնադարյան ճարտարապետության գլուխգործոց։',
      <br />,
      'Այցելելով Սանահին՝ դուք կարող եք տեսնել նաև միջնադարյան հայ ճարտարապետության և արվեստի վառ օրինակները։',
      <br />,
      'Սանահինի համալիրը նաև ներառում է հայտնի Սանահինի կամուրջը, որը կառուցվել է 12-րդ դարում և պահպանվել է մինչև այսօր։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/5/59/Sanahin-external-view.jpg/1920px-Sanahin-external-view.jpg',
  },
  {
    title: 'Գեղարդի վանք',
    description: [
      'Գեղարդի վանական համալիրը հիմնադրվել է 4-րդ դարում և գտնվում է Կոտայքի մարզում։',
      <br />,
      'Վանքը հայտնի է իր ժայռի մեջ կառուցված եկեղեցիներով և խաչքարերով։ Գեղարդի վանքը համարվում է Հայաստանի կարևորագույն հոգևոր կենտրոններից մեկը։',
      <br />,
      'Գեղարդը ընդգրկված է ՅՈՒՆԵՍԿՕ-ի համաշխարհային ժառանգության ցանկում։',
      <br />,
      'Վանքը ստացել է իր անունը այն գեղարդի պատճառով, որը, ըստ ավանդության, օգտագործվել է Քրիստոսի խաչելության ժամանակ։ Այս սրբազան առարկան պահվում է Էջմիածնի տաճարում։',
      <br />,
      'Գեղարդի վանքի տարածքում կարող եք տեսնել բազմաթիվ միջնադարյան խաչքարեր և քարանձավային կառույցներ, որոնք մեծ արժեք ունեն որպես պատմական և մշակութային ժառանգություն։',
      <br />,
      'Գեղարդի վանքն ունի յուրահատուկ ջրաշավիղներ և սրբազան աղբյուրներ, որոնք համարվում են բուժիչ և խորհրդավոր։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/7/7b/Monasterio_de_Geghard%2C_Armenia%2C_2016-10-02%2C_DD_63.jpg/1920px-Monasterio_de_Geghard%2C_Armenia%2C_2016-10-02%2C_DD_63.jpg'
  },
  {
    title: 'Զվարթնոցի տաճար',
    description: [
      'Զվարթնոցի տաճարը, որը կառուցվել է 7-րդ դարում, գտնվել է Արմավիրի մարզում։',
      <br />,
      'Տաճարը մի անզուգական օրինակ է հայկական վաղ միջնադարյան ճարտարապետության և ունի յուրահատուկ դիզայն։',
      <br />,
      'Զվարթնոցը կանգուն է եղել մինչև 10-րդ դարը, երբ երկրաշարժը ավերեց այն։',
      <br />,
      'Այսօր Զվարթնոցի ավերակները ընդգրկված են ՅՈՒՆԵՍԿՕ-ի համաշխարհային ժառանգության ցանկում և բաց են այցելուների համար։',
      <br />,
      'Տաճարի մնացորդները վկայում են նրա հիանալի կառուցվածքի մասին, իսկ Զվարթնոցի թանգարանը ներկայացնում է բազմաթիվ հնագիտական գտածոներ։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/f/ff/Armeni%C3%AB_-_Kaukasus_%282896850946%29.jpg/1920px-Armeni%C3%AB_-_Kaukasus_%282896850946%29.jpg',
  },
  {
    title: 'Ազատ գետի հովիտ',
    description: [
      'Ազատ գետի հովիտը գտնվում է Կոտայքի մարզում և հանդիսանում է Հայաստանի ամենահիասքանչ բնական տարածքներից մեկը։',
      <br />,
      'Հովիտը հայտնի է իր անհավատալի բնական գեղեցկությամբ և տարբերակված լանդշաֆտներով։',
      <br />,
      'Այստեղ գտնվում են բազմաթիվ պատմական և մշակութային հուշարձաններ, ներառյալ Գեղարդի վանքը։',
      <br />,
      'Ազատ գետի հովիտը ընդգրկված է ՅՈՒՆԵՍԿՕ-ի համաշխարհային ժառանգության ցանկում՝ իր մշակութային և բնական արժեքների շնորհիվ։',
      <br />,
      'Հովիտը հայտնի է նաև իր հանքային ջրերով և բարձրադիր ժայռերով, որոնք ապահովում են իդեալական պայմաններ զբոսանքի և արշավների համար։',
      <br />,
      'Այստեղից կարելի է տեսնել Արարատ լեռը, որը ավելի գեղեցիկ է երևում հովտի բարձրադիր կետերից։'
    ],
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/9/99/Azat_at_Garni.jpg/1280px-Azat_at_Garni.jpg',
  }
];

const UnescoPage = () => {
  return (
    <div className="info-page">
      {unesco.map((unesco, index) => (
        <DetailPagee
          key={index}
          title={unesco.title}
          description={unesco.description}
          image={unesco.image}
        />
      ))}
    </div>
  );
};

function Unesco() {
    return (
      <div>
        <PageTitle title='ՅՈՒՆԵՍԿՕ-ի ժառանգություն' />
        <UnescoPage />
      </div>
    );
  }

export { Unesco };
